import xbmc

label = "[script.traktorbeam]: %s"

def info(message):
    xbmc.log(label % message, level=xbmc.LOGNOTICE)

def debug(message):
    xbmc.log(label % message, level=xbmc.LOGDEBUG)

def error(message):
    xbmc.log(label % message, level=xbmc.LOGERROR)

def warning(message):
    xbmc.log(label % message, level=xbmc.LOGWARNING)
